package com.aurea.utils

import com.atlassian.jira.component.ComponentAccessor
import com.atlassian.jira.issue.MutableIssue
import groovy.json.StreamingJsonBuilder;

def issueManager = ComponentAccessor.issueManager

def parentIssue = currentIssue.parentObject;
def customFieldManager = ComponentAccessor.getCustomFieldManager()
def epicCf = customFieldManager.getCustomFieldObjectByName("Epic Link")
def epic = (MutableIssue) parentIssue.getCustomFieldValue(epicCf)

def body_req = [
        "key": "" + epic.key
]

def url = new URL("https://script.google.com/macros/s/AKfycbwAAkhY37zj4CZCziL6QEP6kdnimIdsVDJs71AeJCpEC6zqeVM/exec?key="+epic.key);

URLConnection connection = url.openConnection();
connection.requestMethod = "POST"
connection.doOutput = true
connection.setRequestProperty("Content-Type", "application/json;charset=UTF-8")
connection.outputStream.withWriter("UTF-8") { new StreamingJsonBuilder(it, body_req) }
connection.connect()

log.info("url: " + url)
log.info("Content:" + connection.getContent().getText())
log.info("ResponseCode:" + connection.getResponseCode())
log.info("getResponseMessage:" + connection.getResponseMessage())
